SET NAMES utf8mb4;
SET CHARACTER SET utf8mb4;
SET character_set_connection=utf8mb4;
CREATE TABLE IF NOT EXISTS alembic_version (
	version_num VARCHAR(32) NOT NULL, 
	CONSTRAINT alembic_version_pkc PRIMARY KEY (version_num)
);
CREATE TABLE IF NOT EXISTS table_execute_record (
	id INTEGER NOT NULL, 
	workflow_id INTEGER, 
	prompt_id VARCHAR(100), 
	status VARCHAR(20), 
	result JSON, 
	created_time DATETIME, 
	updated_time DATETIME, 
	user_id VARCHAR(64), 
	execute_timeout INTEGER, is_public INTEGER NOT NULL DEFAULT 0, 
	PRIMARY KEY (id)
);
CREATE TABLE IF NOT EXISTS table_workflow (
	id INTEGER NOT NULL, 
	name VARCHAR(100), 
	`desc` VARCHAR(255), 
	`useTimes` INTEGER, 
	workflow VARCHAR(255), 
	`createdTime` DATETIME, 
	picture VARCHAR(255), 
	`bigPicture` VARCHAR(255), 
	pictures JSON, 
	`flowType` VARCHAR(50), 
	input_schema TEXT, 
	output_schema TEXT, 
	status INTEGER NOT NULL, 
	result_type VARCHAR(20), 
	PRIMARY KEY (id)
);
CREATE TABLE IF NOT EXISTS users (
	id INTEGER NOT NULL, 
	`userId` VARCHAR(64), 
	nickname VARCHAR(50), 
	source VARCHAR(20), 
	external_user_id VARCHAR(64), 
	balance NUMERIC(12, 2), 
	created_time DATETIME, 
	PRIMARY KEY (id)
);
CREATE INDEX ix_users_nickname ON users (nickname);
CREATE INDEX ix_users_source ON users (source);
CREATE INDEX ix_users_external_user_id ON users (external_user_id);
CREATE UNIQUE INDEX `ix_users_userId` ON users (`userId`);
CREATE INDEX ix_users_id ON users (id);
CREATE INDEX ix_table_workflow_id ON table_workflow (id);
CREATE INDEX ix_table_workflow_name ON table_workflow (name);
CREATE INDEX ix_table_execute_record_prompt_id ON table_execute_record (prompt_id);
CREATE INDEX ix_table_execute_record_workflow_id ON table_execute_record (workflow_id);
CREATE INDEX ix_table_execute_record_user_id ON table_execute_record (user_id);
CREATE INDEX ix_table_execute_record_id ON table_execute_record (id);
